import './App.css';
import React, {Component} from 'react';
import {CardList} from './components/card-list/card-list.component';

class App extends Component {
  constructor() {
    super();

    this.state = {
      heroes: [],
      searchField: ''
    }
  }

  componentDidMount() {
    fetch('https://api.opendota.com/api/heroStats')
    .then(response => response.json())
    .then(users => this.setState({heroes: users}))
  }

  render() {
    return(
      <div className="App">
        <input type='search' placeholder='search heroes' onChange={e => {this.setState({searchField: e.target.value}, () => console.log(this.state));}} />
        <CardList heroes = {this.state.heroes} />
    </div>
    );
  }
}

export default App;
